function run() {

    const git = {{respostavalor}};

    const gitDictionary = arrayToDictionary(git);

    return JSON.stringify(gitDictionary);

}



function arrayToDictionary(array) {

    const dictionary = {};

    array.forEach((value, index) => {

        dictionary[index] = value;

    });

    return dictionary;

}
